mapper.select:
select member_id, member_enrolldate
	from member
where
	member_phone = #{phone}

	and member_name = #{name}


	and member_id = #{id}

select count(*)
from authPhone
where phone = #{phone}
and authcode = #{authCode}
and flag = #{flag}

		 select member_id, member_address, address.member_address2, address_name, 0 as flag from address where member_id = #{memberId}
        union
        select member_id, member_address, member_address2, null, 1 from member where member_id = #{memberId}
        order by flag desc

select * from member_auto_login
where cookie_key = #{cookieKey}
and last_date + 7 > sysdate

mapper.update:
update member_auto_login
set last_date = sysdate
where cookie_key = #{cookieKey}
