mapper.select:
select count(*)
from authPhone
where phone = #{phone}
and authcode = #{authCode}

		 select member_id, member_address, address.member_address2, address_name, 0 as flag from address where member_id = #{memberId}
        union
        select member_id, member_address, member_address2, null, 1 from member where member_id = #{memberId}
        order by flag desc
